import { createApp } from 'vue'
import App from './App.vue'
import router from './router'
import axios from 'axios'

// Before axios
// createApp(App).use(router).mount('#app')

const app = createApp(App);
app.use(router);

// Create an Axios instance with your configuration
const apiClient = axios.create({
  baseURL: 'http://localhost:3000/api', // Your backend API base URL
  // Add any other default configurations here (e.g., headers, timeout)
});

// Provide the Axios instance to your Vue app
app.config.globalProperties.$http = apiClient;

app.mount('#app');
